$(document).ready(function(){var role=$(".card").attr("role");post("http://s.chengxinxitong.com/app1.0.0/getUserInfoData.action",{identify:get_identify_safe()},function(resp){var resp=eval("("+resp+")");return 0!=resp.errno&&"error"in resp?(alert(resp.error),!1):void("family"==role?$(".card .balance").text(resp.family_money):"personal"==role&&$(".card .balance").text(resp.personal_money))})});